-- Get the players service local players = game:GetService("Players") local RunService = game:GetService("RunService") local UserInputService = game:GetService("UserInputService") -- Variable to control the ESP state local espEnabled = false local flyEnabled = false local noclipEnabled = false -- Function to get a player's Brainrots local function getBrainrots(player) -- Assuming Brainrots are stored in a table in the player's character local character = player.Character if character then local brainrots = character:FindFirstChild("Brainrots") if brainrots then return brainrots:GetChildren() end end return {} end -- Create a function to draw ESP boxes around Brainrots local function drawESP(brainrot) local esp = Drawing.new("Text") esp.Text = brainrot.Name esp.Color = Color3.new(1, 0, 0) esp.Size = 16 esp.Center = true esp.Outline = true esp.OutlineColor = Color3.new(0, 0, 0) esp.Position = Vector2.new(brainrot.Position.X, brainrot.Position.Y) RunService.RenderStepped:Connect(function() local position = brainrot.Position local viewportPosition = workspace.CurrentCamera:WorldToViewportPoint(position) esp.Position = Vector2.new(viewportPosition.X, viewportPosition.Y - 20) -- Adjust the text position end) return esp end -- Function to show ESP for Brainrots local function showESP() for _, player in pairs(players:GetPlayers()) do local brainrots = getBrainrots(player) for _, brainrot in pairs(brainrots) do local esp = drawESP(brainrot) while espEnabled do wait() end esp:Remove() end end end -- Function to toggle ESP local function toggleESP() espEnabled = not espEnabled if espEnabled then -- Enable ESP while espEnabled do showESP() wait() end else -- Disable ESP for _, drawing in pairs(Drawing.getAll()) do drawing:Remove() end end end -- Function to fly local function fly() local character = players.LocalPlayer.Character if character then local humanoidRootPart = character:FindFirstChild("HumanoidRootPart") if humanoidRootPart then while flyEnabled do humanoidRootPart.Velocity = Vector3.new(0, 5, 0) wait() end end end end -- Function to toggle fly local function toggleFly() flyEnabled = not flyEnabled if flyEnabled then -- Enable fly fly() else -- Disable fly local character = players.LocalPlayer.Character if character then local humanoidRootPart = character:FindFirstChild("HumanoidRootPart") if humanoidRootPart then humanoidRootPart.Velocity = Vector3.new(0, 0, 0) end end end end -- Function for noclip local function noclip() local character = players.LocalPlayer.Character if character then for _, part in pairs(character:GetDescendants()) do if part:IsA("BasePart") then part.CanCollide = false end end end end -- Function to toggle noclip local function toggleNoclip() noclipEnabled = not noclipEnabled if noclipEnabled then -- Enable noclip RunService.Stepped:Connect(noclip) else -- Disable noclip RunService.Stepped:Disconnect(noclip) local character = players.LocalPlayer.Character if character then for _, part in pairs(character:GetDescendants()) do if part:IsA("BasePart") then part.CanCollide = true end end end end end -- Configure the buttons to toggle the functions UserInputService.InputBegan:Connect(function(input) if input.KeyCode == Enum.KeyCode.F6 then -- Change the key as needed toggleESP() elseif input.KeyCode == Enum.KeyCode.F7 then -- Change the key as needed toggleFly() elseif input.KeyCode == Enum.KeyCode.F8 then -- Change the key as needed toggleNoclip() end end)
